package Programmin_Basics;

public class ASCII_Cast_Conversions {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Convert from char to ascii
		char ch='d';
		int ascii=ch;
		System.out.println("The char value is: "+ch);
		System.out.println("The ascii character is "+ascii);
		
		//Conver from ascii to character
		int asciii=99;
		char ch1=(char) asciii;
		System.out.println("The char value is: "+ch1);
		System.out.println("The ascii character is "+asciii);
		

	}

}
