package Programmin_Basics;

public class Input_Output {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     System.out.print("This is about Java.");//Prints in the same line
     System.out.println("Everyone is so interested in.");//Prints in different/next line
     System.out.printf("Lets learn together.");
     System.out.println("Lets Rollllllll");
     
     //Print Variables and Literals:
     double doublenumber=-33;
     
     System.out.println(doublenumber);//Prints variable value
     System.out.println(66);//Prints literal
     
     //Print Concatenated Strings:
   //Concatenation of string and double or concatenation of two different data types
     System.out.println("I am Awesome number:"+doublenumber);
     System.out.println("Its a String"+" Concatenation");
     
     
     
     
	}

}
