package Programmin_Basics;

public class Quotient_Reminder {
	//Variables declaring outside the method
	public static int first=33;
	static int second=22;
    static int quotient=first/second;
	static int reminder=first%second;
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println(quotient);
		System.out.println(reminder);

	}

}
