package decisionmaking_loops;

public class print_Alphabets {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		char c;
		//To print upper case letters
        for (c='A';c<='Z';c++) {
        	System.out.print(c+ " ");
        }
        System.out.println();
        //To print small letters
        char d;
        for (d='a';d<='z';d++) {
        	System.out.print(d+ " ");
        }
	}

}
